define(function(require,exports,module){var $=require("jquery");var Timer=require("common/timer.min");var util=require("common/util.min");function countdown(target,time,now,endtime,method,callback){count();setInterval(count,time);function count(){now=parseInt(now)+time;$(target).each(function(){var n=new Date();var e=new Date();n.setTime(parseInt(now));e.setTime(parseInt(endtime));var t=new Timer(n,e);$(this).html(util.call(t,method));if(callback){callback()}})}}module.exports.timer=Timer;module.exports.countdown=countdown});